import styles from './MenuPages.module.css'
import logo from '../image/logo.png'
import { Link } from 'react-router-dom'
import { useEffect, useState } from 'react'

function MenuPages(){

    const [ carregaPagina, setCarregaPagina] = useState(false)

    const windowUnload = () => {
        setCarregaPagina(true)

        if(carregaPagina){
            window.scrollTo(0,0)
        }
    }

    useEffect(() =>{
        window.addEventListener('load', windowUnload)
    },[carregaPagina])

    return(
        <header id='top' className={styles.header}>
        <nav className={styles.nav}>
            <a href="../index.html" className={styles.logo}> <img src={logo} alt="Likizoa" /> </a>
            <Link to='/' className={styles.voltar}><i className="fas fa-arrow-left"></i> Voltar</Link>
        </nav>
    </header>
    )
}

export default MenuPages
