import React from "react"
import { useEffect } from "react"

import styles from './NossaEmpresaPage.module.css'

import MenuPages from "../MenuPages"
import NossaEmpresaSections from "../sectionsNossaEmpresa/NossaEmpresaSections"


function NossaEmpresaPage() {

    const [ carregaPagina, setCarregaPagina] = useState(false)

    const windowUnload = () => {
        setCarregaPagina(true)

        if(carregaPagina){
            window.scrollTo(0,0)
        }
    }

    useEffect(() =>{
        window.addEventListener('load', windowUnload)
    },[carregaPagina])

    return (
        <div className={styles.body}>

            <MenuPages />
            <NossaEmpresaSections />

        </div>

    )
}

export default NossaEmpresaPage
