import { useEffect, useState, useRef } from "react";

import styles from "./MenuHome.module.css";
import logo from "../image/logo.png";

function MenuHome() {
    const burguerRef = useRef(null);
    const fecharRef = useRef(null);
    const menuRef = useRef(null);
    const listaMenuRef = useRef(null);
    const navMenuRef = useRef(null);

    const [fecharClass, setFecharClass] = useState(`${styles.fechado}`);
    const [burguerClass, setBurguerClass] = useState(`${styles.burguer}`);
    const [menuAberto, setMenuAberto] = useState(false);

    const tamanhoTela = () => {
        if (window.innerWidth <= 1100) {
            setBurguerClass(`${styles.burguer}`);
            setFecharClass(`${styles.fechado}`);
            if (menuRef.current) {
                menuRef.current.style.height = '0%';
                listaMenuRef.current.style.display = 'none';
            }
        } else {
            if (menuRef.current) {
                menuRef.current.style.height = '100px';
                listaMenuRef.current.style.display = 'flex';
                listaMenuRef.current.style.opacity = 1;
            }
        }
    };

    useEffect(() => {
        window.addEventListener('resize', tamanhoTela);
        tamanhoTela(); // Ajusta o menu ao carregar

        return () => {
            window.removeEventListener('resize', tamanhoTela);
        };
    }, []);

    const handleClick = () => {
        if (window.innerWidth < 1100) {
            setMenuAberto(false);
            if (menuRef.current) {
                menuRef.current.style.height = '0%';
                navMenuRef.current.style.height = '0%';
            }
            setTimeout(() => {
                if (listaMenuRef.current) {
                    listaMenuRef.current.style.opacity = 0;
                    setBurguerClass(`${styles.burguer}`);
                }
            }, 200);
            setTimeout(() => {
                if (listaMenuRef.current) {
                    listaMenuRef.current.style.display = 'none';
                }
            }, 700);
        }
    };

    const abrirMenu = () => {
        setMenuAberto(true);
        if (menuRef.current) {
            menuRef.current.style.height = '100%';
        }
        setBurguerClass(`${styles.burguer} ${styles.giraCloseD}`);
        setFecharClass(`${styles.fechado} ${styles.giraOpenD}`);
        navMenuRef.current.style.height = '100%';

        setTimeout(() => {
            if (listaMenuRef.current && fecharRef.current) {
                listaMenuRef.current.style.display = 'block';
                fecharRef.current.style.display = 'block';
            }
        }, 200);

        setTimeout(() => {
            if (listaMenuRef.current) {
                listaMenuRef.current.style.opacity = 1;
            }
        }, 500);
    };

    useEffect(() => {
        const burguer = burguerRef.current;
        if (burguer) {
            burguer.addEventListener('click', abrirMenu);
        }

        return () => {
            if (burguer) {
                burguer.removeEventListener('click', abrirMenu);
            }
        };
    }, [burguerClass]);

    return (
        <nav ref={navMenuRef} className={styles.nav_menu}>
            <div id="header">
                <a href="#welcome"> <img className={styles.img_logo} src={logo} alt="Likizoa" /> </a>
                <svg ref={burguerRef} className={burguerClass} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24"
                    color="#000000" fill="none">
                    <path d="M4 5L20 5" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                    <path d="M4 12L20 12" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                    <path d="M4 19L20 19" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                </svg>
                <svg ref={fecharRef} className={fecharClass} style={{ display: menuAberto ? 'block' : 'none' }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24"
                    color="#ffffff" fill="none">
                    <path d="M19.0005 4.99988L5.00049 18.9999M5.00049 4.99988L19.0005 18.9999" stroke="currentColor"
                        strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
                </svg>
            </div>
            <menu ref={menuRef} className={styles.menu}>
                <ul ref={listaMenuRef} className={styles.lista_menu}>
                    <a href="#capa">
                        <li className={styles.itens_menu} onClick={handleClick}>Início</li>
                    </a>
                    <a href="#nossa_empresa">
                        <li className={styles.itens_menu} onClick={handleClick}>Nossa Empresa</li>
                    </a>
                    <a href="#parceiros">
                        <li className={styles.itens_menu} onClick={handleClick}>Parceiros</li>
                    </a>
                    <a href="#contatos">
                        <li className={styles.itens_menu} onClick={handleClick}>Contato</li>
                    </a>
                    <a href="#">
                        <li className={styles.itens_menu} onClick={handleClick}>Teste</li>
                    </a>
                    <a href="/pages/login.html">
                        <li className={styles.itens_menu} onClick={handleClick}>Login</li>
                    </a>
                </ul>
            </menu>
        </nav>
    );
}

export default MenuHome;
