import { useEffect, useState, useRef } from "react"; // Importação de hooks do React

import styles from "./MenuHome.module.css"; // Importa os estilos específicos para o componente
import logo from "../image/logo.png"; // Importa a imagem do logo

function MenuHome() {
    // Referências para elementos DOM específicos
    const burguerRef = useRef(null); // Referência para o ícone de "hambúrguer" (menu)
    const fecharRef = useRef(null); // Referência para o ícone de "fechar"
    const menuRef = useRef(null); // Referência para o elemento <menu>
    const listaMenuRef = useRef(null); // Referência para a lista de itens de menu
    const navMenuRef = useRef(null); // Referência para o elemento <nav>

    // Estados para controlar classes e visibilidade dos elementos
    const [fecharClass, setFecharClass] = useState(`${styles.fechado}`); // Classe do ícone de "fechar"
    const [burguerClass, setBurguerClass] = useState(`${styles.burguer}`); // Classe do ícone de "hambúrguer"
    const [fecharVisible, setfecharVisible] = useState(false); // Estado para saber se o menu está aberto

    // Função que ajusta a visualização dos elementos com base no tamanho da tela
    const tamanhoTela = () => {
        if (window.innerWidth <= 1100) {
            // Se a largura da tela for menor ou igual a 1100px
            setBurguerClass(`${styles.burguer}`);
            setFecharClass(`${styles.fechado}`);
            if (menuRef.current) {
                menuRef.current.style.height = '0%'; // Fecha o menu
                listaMenuRef.current.style.display = 'none'; // Esconde a lista de itens do menu
            }
        } else {
            // Se a largura da tela for maior que 1100px
            if (menuRef.current) {
                menuRef.current.style.height = '100px'; // Define a altura do menu
                listaMenuRef.current.style.display = 'flex'; // Exibe a lista de itens do menu
                listaMenuRef.current.style.opacity = 1; // Define a opacidade para 100%
            }
        }
    };

    // Hook para monitorar mudanças no tamanho da tela e ajustar o menu
    useEffect(() => {
        window.addEventListener('resize', tamanhoTela); // Adiciona o listener para redimensionamento da tela
        tamanhoTela(); // Ajusta o menu ao carregar

        // Limpa o listener ao desmontar o componente
        return () => {
            window.removeEventListener('resize', tamanhoTela);
        };
    }, []); // O array vazio indica que o efeito será executado apenas uma vez

    // Função chamada quando o menu é clicado (em dispositivos móveis)
    const handleClick = () => {
        if (window.innerWidth < 1100) {
            // Fecha o menu se a largura da tela for menor que 1100px
            setfecharVisible(false);
            if (menuRef.current) {
                menuRef.current.style.height = '0%';
                navMenuRef.current.style.height = '0%';
            }
            setTimeout(() => {
                if (listaMenuRef.current) {
                    listaMenuRef.current.style.opacity = 0;
                    setBurguerClass(`${styles.burguer}`);
                }
            }, 200);
            setTimeout(() => {
                if (listaMenuRef.current) {
                    listaMenuRef.current.style.display = 'none';
                }
            }, 700);
        }
    };

    // Função chamada para abrir o menu
    const abrirMenu = () => {
        setfecharVisible(true); // Marca o X como aberto
        if (menuRef.current) {
            menuRef.current.style.height = '100%';
        }
        setBurguerClass(`${styles.burguer} ${styles.giraCloseD}`);
        setFecharClass(`${styles.fechado} ${styles.giraOpenD}`);
        navMenuRef.current.style.height = '100%';

        setTimeout(() => {
            if (listaMenuRef.current && fecharRef.current) {
                listaMenuRef.current.style.display = 'block'; // Exibe a lista de itens
                fecharRef.current.style.display = 'block'; // Exibe o ícone de "fechar"
            }
        }, 200);

        setTimeout(() => {
            if (listaMenuRef.current) {
                listaMenuRef.current.style.opacity = 1; // Define a opacidade para 100%
            }
        }, 500);
    };

    // Hook para adicionar/remover o listener de clique no ícone de "hambúrguer"
    useEffect(() => {
        const burguer = burguerRef.current;
        if (burguer) {
            burguer.addEventListener('click', abrirMenu);
        }

        // Remove o listener ao desmontar o componente ou quando o estado burguerClass mudar
        return () => {
            if (burguer) {
                burguer.removeEventListener('click', abrirMenu);
            }
        };
    }, [burguerClass]); // O efeito é executado sempre que burguerClass muda

    const fecharMenu = () =>{
        if(menuRef.current){
            menuRef.current.style.height = '0%'
        }
        setFecharClass(`${styles.fechado} ${styles.giraCloseE}`)
        navMenuRef.current.style.height = '0%'

        setTimeout(() => {
            listaMenuRef.current.style.opacity = '0%'
            setBurguerClass(`${styles.burguer} ${styles.giraOpenE}`)
        }, 200)
    }


    // JSX para renderizar o componente de menu
    return (
        <nav ref={navMenuRef} className={styles.nav_menu}>
            <div id="header">
                {/* Logo da empresa com um link para o topo da página */}
                <a href="#welcome"> <img className={styles.img_logo} src={logo} alt="Likizoa" /> </a>
                {/* Ícone de "menu hambúrguer" (3 linhas) usado para abrir o menu */}
                <svg ref={burguerRef} className={burguerClass} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24"
                    color="#000000" fill="none">
                    <path d="M4 5L20 5" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                    <path d="M4 12L20 12" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                    <path d="M4 19L20 19" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                </svg>
                {/* Ícone de "fechar" (X) que aparece após abrir o menu */}
                <svg ref={fecharRef} className={fecharClass} style={{ display: fecharVisible ? 'block' : 'none' }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24"
                    color="#ffffff" fill="none">
                    <path d="M19.0005 4.99988L5.00049 18.9999M5.00049 4.99988L19.0005 18.9999" stroke="currentColor"
                        strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
                </svg>
            </div>
            <menu ref={menuRef} className={styles.menu}>
                <ul ref={listaMenuRef} className={styles.lista_menu}>
                    <a href="#capa">
                        <li className={styles.itens_menu} onClick={handleClick}>Início</li>
                    </a>
                    <a href="#nossa_empresa">
                        <li className={styles.itens_menu} onClick={handleClick}>Nossa Empresa</li>
                    </a>
                    <a href="#parceiros">
                        <li className={styles.itens_menu} onClick={handleClick}>Parceiros</li>
                    </a>
                    <a href="#contatos">
                        <li className={styles.itens_menu} onClick={handleClick}>Contato</li>
                    </a>
                    <a href="#">
                        <li className={styles.itens_menu} onClick={handleClick}>Teste</li>
                    </a>
                    <a href="/pages/login.html">
                        <li className={styles.itens_menu} onClick={handleClick}>Login</li>
                    </a>
                </ul>
            </menu>
        </nav>
    );
}

export default MenuHome;
