import { useState, useEffect } from "react"

import styles from "./MenuHome.module.css"
import logo from "../image/logo.png"

function MenuHome() {

    let burguer = document.querySelector(`.${styles.burguer}`)
    let fechar = document.querySelector(`.${styles.fechado}`)
    let menu = document.querySelector(`.${styles.menu}`)
    let listaMenu = document.querySelector(`.${styles.lista_menu}`)
    let itensMenu = document.querySelectorAll(`.${styles.itens_menu}`)
    let navMenu = document.querySelector(`.${styles.nav_menu}`)

    function tamanhoTela() {



        if (!burguer || !fechar || !menu || !listaMenu) return;

        if (window.innerWidth <= 1100) {
            burguer.classList.remove('giraCloseD')
            burguer.style.display = 'block'
            menu.style.height = '0%'
            listaMenu.style.display = 'none'
        } else {

            burguer.style.display = 'none'
            fechar.style.display = 'none'
            menu.style.height = '100px'
            menu.style.flexDirection = 'row'
            listaMenu.style.display = 'flex'
            listaMenu.style.opacity = 1
        }
    }

    useEffect(() => {
        window.addEventListener('resize', tamanhoTela)
        window.addEventListener('load', tamanhoTela)

        return () => {
            window.removeEventListener('resize', tamanhoTela)
            window.removeEventListener('load', tamanhoTela)
        }
    })

    const handleClick = () => {
        
    }

    return (
        <nav className={styles.nav_menu}>
            <div id="header">
                {/* <!-- Logo da empresa com um link para o topo da página --> */}
                <a href="#welcome"> <img className={styles.img_logo} src={logo} alt="Likizoa" /> </a>
                {/* <!-- Ícone de "menu hambúrguer" (3 linhas) usado para abrir o menu em dispositivos móveis --> */}
                <svg className={styles.burguer} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24"
                    color="#000000" fill="none">
                    <path d="M4 5L20 5" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                    <path d="M4 12L20 12" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                    <path d="M4 19L20 19" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"
                        strokeLinejoin="round" />
                </svg>
                {/* <!-- Ícone de "fechar" o menu (um X) que aparece após abrir o menu em dispositivos móveis --> */}
                <svg className={styles.fechado} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24"
                    color="#ffffff" fill="none">
                    <path d="M19.0005 4.99988L5.00049 18.9999M5.00049 4.99988L19.0005 18.9999" stroke="currentColor"
                        strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
                </svg>
            </div>
            {/* <!-- Menu de navegação com links para diferentes seções da página --> */}
            <menu className={styles.menu}>
                <ul className={styles.lista_menu}>
                    {/* <!-- Link para a seção inicial (Início) --> */}
                    <a href="#capa">
                        <li className={styles.itens_menu}>Início</li>
                    </a>
                    {/* <!-- Link para a seção da empresa (placeholder, sem referência) --> */}
                    <a href="#nossa_empresa">
                        <li className={styles.itens_menu}>Nossa Empresa</li>
                    </a>
                    {/* <!-- Link para a seção do aplicativo (placeholder, sem referência) --> */}
                    <a href="#parceiros">
                        <li className={styles.itens_menu}>Parceiros</li>
                    </a>
                    {/* <!-- Link para a seção de contato (placeholder, sem referência) --> */}
                    <a href="#contatos">
                        <li className={styles.itens_menu}>Contato</li>
                    </a>
                    {/* <!-- Link para a seção "Sobre o aplicativo" (Nossa História) --> */}
                    <a href="#">
                        <li className={styles.itens_menu}>teste</li>
                    </a>
                    {/* <!-- Link para a seção "Sobre" (Nossa História) --> */}
                    <a href="/pages/login.html">
                        <li className={styles.itens_menu}>Login</li>
                    </a>
                </ul>
            </menu>
        </nav>
    )
}

export default MenuHome
