import React, { useEffect, useState } from "react";
import MenuPages from "../MenuPages";
import styles from "./Login.module.css";
import ScrollToTop from "../ScrollToTop";
import ComponentsLogin from "../ComponentsLogin/ComponentsLogin";

// Importações Firebase (use as variáveis do .env, vou te mostrar como depois)
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore";

// Função para adicionar animação de fade-in
function fadeIn(element) {
    element.style.opacity = 0;
    element.style.display = "block";
    let op = 0.1;  // Inicializa a opacidade
    const timer = setInterval(() => {
        if (op >= 1) {
            clearInterval(timer);
        }
        element.style.opacity = op;
        op += op * 0.1;
    }, 10);
}

// Função para adicionar animação de fade-out
function fadeOut(element) {
    let op = 1;  // Inicializa a opacidade em 1
    const timer = setInterval(() => {
        if (op <= 0.1) {
            clearInterval(timer);
            element.style.display = "none";
        }
        element.style.opacity = op;
        op -= op * 0.1;
    }, 10);
}

// Configuração Firebase com variáveis de ambiente
const firebaseConfig = {
    apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
    authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
    projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
    storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
    messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
    appId: process.env.REACT_APP_FIREBASE_APP_ID,
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

function Login() {
    const [userType, setUserType] = useState('');
    const [clientes, setClientes] = useState([]);
    const [funcionarios, setFuncionarios] = useState([]);

    // Carregar dados do Firestore
    const carregarDados = async () => {
        try {
            const clientesSnapshot = await getDocs(collection(db, "clientes"));
            const funcionariosSnapshot = await getDocs(collection(db, "funcionarios"));
            setClientes(clientesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
            setFuncionarios(funcionariosSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        } catch (error) {
            console.error("Erro ao buscar dados do Firestore:", error);
        }
    };

    useEffect(() => {
        carregarDados();
    }, []);

    const handleUserTypeChange = (e) => {
        setUserType(e.target.value);
        const clienteSection = document.getElementById('cliente-section');
        const funcionarioSection = document.getElementById('funcionario-section');
        
        if (e.target.value === "cliente") {
            fadeOut(funcionarioSection);
            fadeIn(clienteSection);
        } else if (e.target.value === "funcionario") {
            fadeOut(clienteSection);
            fadeIn(funcionarioSection);
        } else {
            fadeOut(clienteSection);
            fadeOut(funcionarioSection);
        }
    };


    return(
        <div className={styles.body}>
            <ScrollToTop />
            <MenuPages />
            <ComponentsLogin />
        </div>
    )
}

export default Login
